/****
 * Formats a number according to the language convention
 * Parameters
 *   - f: the number to format
 *   - $decimals: number of digits
 * Created: 03/2009, following tip at
 *    http://fredericiana.com/2008/05/29/php-multi-byte-safe-number_format-function/#more-1287
 */
function format_float($f, $decimals=0) {
	$phpTANumberFormat = array(","," "); // Decimal separator, thousands separator
	$placeholders = array('@', '~');
	// format number with placeholders
	$formatted = number_format($f, $decimals, $placeholders[0], $placeholders[1]);
	// replace by localized characters
	$formatted = str_replace($placeholders, $phpTANumberFormat, $formatted);
	return $formatted;
}

/****
 * Returns a string such as 1 hit day if singular or 
 * 15 hits for plural.
 * Can be adapted for languages with complicated purals, such as Polish
 * Parameters
 *   - n: the number to format
 * Created: 03/2009
 */
function hits($n) {
	$num = format_float($n, 0);
	if ($n > 1) return "$num accès";
	return "$num accès";
}

/****
 * Returns a string such as 1 visit if singular or 
 * 15 visits for plural.
 * Can be adapted for languages with complicated purals, such as Polish
 * Parameters
 *   - n: the number to format
 * Created: 03/2009
 */
function visits($n) {
	$num = format_float($n, 0);
	if ($n > 1) return "$num visites";
	return "$num visite";
}

/****
 * Returns a string such as 1 page click/ visit if singular or 
 * 15 clicks/ visit for plural.
 * Can be adapted for languages with complicated purals, such as Polish
 * Parameters
 *   - f: the number to format
 *   - $decimals: number of digits
 * Created: 03/2009
 */
function clickspervisits($n,$decimals=3) {
	$num = format_float($n, $decimals);
	if ($n > 1) return "$num clics/ visite";
	return "$num clic/ visite";
}

/****
 * Returns a string such as 1 page view if singular or 
 * 15 page views for plural.
 * Can be adapted for languages with complicated purals, such as Polish
 * Parameters
 *   - n: the number to format
 * Created: 03/2009
 */
function pageviews($n) {
	$num = format_float($n, 0);
	if ($n > 1) return "$num page vues";
	return "$num page vue";
}

/****
 * Returns a string such as 1 page view/ day if singular or 
 * 15 page views/ day for plural.
 * Can be adapted for languages with complicated purals, such as Polish
 * Parameters
 *   - f: the number to format
 *   - $decimals: number of digits
 * Created: 03/2009
 */
function pageviewsperday($f, $decimals=0) {
	$number = format_float($f, $decimals);
	if (($f <= 1) && ($decimals == 0)) return "$number page vue/ jour";
	return "$number pages vues/ jour";
}

/****
 * Returns a string such as 1 visitor if singular or 
 * 15 visitors for plural.
 * Can be adapted for languages with complicated purals, such as Polish
 * Parameters
 *   - n: the number to format
 * Created: 03/2009
 */
function visitors($n) {
	$num = format_float($n, 0);
	if ($n > 1) return "$num visiteurs";
	return "$num visiteur";
}

/****
 * Returns a string such as 1 visitor/ day if singular or 
 * 15 visitors/ day for plural.
 * Can be adapted for languages with complicated purals, such as Polish
 * Parameters
 *   - f: the number to format
 *   - $decimals: number of digits
 * Created: 03/2009
 */
function visitorsperday($f, $decimals=0) {
	$number = format_float($f, $decimals);
	if (($f <= 1) && ($decimals == 0)) return "$number visiteur/ jour";
	return "$number visiteurs/ jour";
}

/************************************************************************
 Returns a date with day, month, and year
 - phrase=0: just the date
 - phrase=1: in a phrase (not important in English, but important for other languages)
 - phrase=1: at the begining of a phrase (not important in English, but important for other languages)
*************************************************************************/
function dayMmYear ($time, $phrase = 0) {
	$monthArr = array ("", "janvier", "février", "mars", "avril", "mai", "juin", "juillet", "août", "septembre", "octobre", "novembre", "décembre");
	$date = date("d", $time)." ".$monthArr[date("n", $time)]." ".date("Y", $time);
	if ($phrase == 1) {
		$date = "le $date";
	} else if ($phrase == 2) {
		$date = "Le $date";
	}
	return $date;
}

/************************************************************************
 Returns a date with day, month, and year, and time
 - phrase=0: just the date
 - phrase=1: in a phrase (not important in English, but important for other languages)
 - phrase=1: at the begining of a phrase (not important in English, but important for other languages)
*************************************************************************/
function dateandtime ($time, $phrase = 0) {
	$monthArr = array ("", "janvier", "février", "mars", "avril", "mai", "juin", "juillet", "août", "septembre", "octobre", "novembre", "décembre");
	$date = date("d", $time)." ".$monthArr[date("n", $time)]." ".date("Y, H:i", $time);
	if ($phrase == 1) {
		$date = "le $date";
	} else if ($phrase == 2) {
		$date = "Le $date";
	}
	return $date;
}

/************************************************************************
 Returns month and year
*************************************************************************/
function monthYear ($time) {
	$monthArr = array ("", "janvier", "février", "mars", "avril", "mai", "juin", "juillet", "août", "septembre", "octobre", "novembre", "décembre");
	$date = $monthArr[date("n", $time)].date(" Y", $time);
	return $date;
}

/************************************************************************
 Returns month and year as Jan-06 for instance
*************************************************************************/
function shortMonthYear($time) {
	global $strings;
	$monthArr = array("", $strings['jan'], $strings['feb'], $strings['mar'], $strings['apr'], $strings['ma'], $strings['jun'], $strings['jul'], $strings['aug'], $strings['sep'], $strings['oct'], $strings['nov'], $strings['dec']);
	return $monthArr[date("n", $time)]." ".date("y", $time);
}

/************************************************************************
 Returns a time
 - phrase=0: just the date
 - phrase=1: in a phrase
 - phrase=1: at the begining of a phrase
*************************************************************************/
function timetoday ($time, $phrase = 0) {
	$date = date("H", $time)."h".date("i", $time);
	if ($phrase == 1) {
		$date = "aujourd'hui, $date";
	} else if ($phrase == 2) {
		$date = "Aujourd'hui, $date";
	}
	return $date;
}

